import React, { useState } from 'react';
import './Reviews.css';

const reviews = [
    { id: 1, date: '2024-06-01', customerName: 'Jane Doe', product: 'Chocolate Cake', rating: 5, review: 'Amazing chocolate cake!', imageUrl: '/images/customer-1.jpg' },
    { id: 2, date: '2024-06-02', customerName: 'John Smith', product: 'Vanilla Cupcake', rating: 4, review: 'Loved the vanilla cupcake!', imageUrl: '/images/customer-2.jpg' },
    { id: 3, date: '2024-06-03', customerName: 'Alice Johnson', product: 'Blueberry Muffin', rating: 4.2, review: 'The blueberry muffin was fresh and delicious.', imageUrl: '/images/customer-3.jpg' },
    { id: 4, date: '2024-06-04', customerName: 'Michael Brown', product: 'Red Velvet Cake', rating: 4.8, review: 'The red velvet cake was divine!', imageUrl: '/images/customer-4.jpg' },
    { id: 5, date: '2024-06-05', customerName: 'Emily Davis', product: 'Strawberry Tart', rating: 4.5, review: 'The strawberry tart was delightful!', imageUrl: '/images/customer-5.jpg' },
    { id: 6, date: '2024-06-06', customerName: 'David Wilson', product: 'Lemon Meringue Pie', rating: 4.7, review: 'Tangy and sweet lemon meringue pie.', imageUrl: '/images/customer-6.jpg' },
    { id: 7, date: '2024-06-07', customerName: 'Sarah Lee', product: 'Chocolate Chip Cookie', rating: 4.3, review: 'Classic chocolate chip cookies, loved it!', imageUrl: '/images/customer-7.jpg' },
    { id: 8, date: '2024-06-08', customerName: 'Chris Green', product: 'Raspberry Cheesecake', rating: 4.9, review: 'The raspberry cheesecake was heavenly!', imageUrl: '/images/customer-8.jpg' },
    { id: 9, date: '2024-06-09', customerName: 'Laura White', product: 'Almond Croissant', rating: 4.4, review: 'Buttery and delicious almond croissant.', imageUrl: '/images/customer-9.jpg' },
    { id: 10, date: '2024-06-10', customerName: 'James Clark', product: 'Banana Bread', rating: 4.5, review: 'Moist and flavorful banana bread.', imageUrl: '/images/customer-10.jpg' },
    { id: 11, date: '2024-06-11', customerName: 'Patricia King', product: 'Apple Pie', rating: 4.6, review: 'Classic apple pie, just like grandma used to make.', imageUrl: '/images/customer-11.jpg' },
    { id: 12, date: '2024-06-12', customerName: 'Robert Harris', product: 'Carrot Cake', rating: 4.7, review: 'Spiced carrot cake with perfect cream cheese frosting.', imageUrl: '/images/customer-12.jpg' },
    { id: 13, date: '2024-06-13', customerName: 'Linda Martinez', product: 'Peach Cobbler', rating: 4.5, review: 'Sweet and delicious peach cobbler.', imageUrl: '/images/customer-13.jpg' },
    { id: 14, date: '2024-06-14', customerName: 'Steven Allen', product: 'Pumpkin Pie', rating: 4.6, review: 'Creamy and flavorful pumpkin pie.', imageUrl: '/images/customer-14.jpg' },
    { id: 15, date: '2024-06-15', customerName: 'Karen Scott', product: 'Cherry Danish', rating: 4.4, review: 'Flaky and delicious cherry danish.', imageUrl: '/images/customer-15.jpg' },
    { id: 16, date: '2024-06-16', customerName: 'George Hall', product: 'Chocolate Cake', rating: 4.8, review: 'Best chocolate cake ever!', imageUrl: '/images/customer-16.jpg' },
    { id: 17, date: '2024-06-17', customerName: 'Nancy Young', product: 'Vanilla Cupcake', rating: 4.2, review: 'Nice and fluffy vanilla cupcakes.', imageUrl: '/images/customer-17.jpg' },
    { id: 18, date: '2024-06-18', customerName: 'Mark Nelson', product: 'Blueberry Muffin', rating: 4.3, review: 'Really enjoyed the blueberry muffin.', imageUrl: '/images/customer-18.jpg' },
    { id: 19, date: '2024-06-19', customerName: 'Lisa Carter', product: 'Red Velvet Cake', rating: 4.9, review: 'Absolutely loved the red velvet cake.', imageUrl: '/images/customer-19.jpg' },
    { id: 20, date: '2024-06-20', customerName: 'Paul Edwards', product: 'Strawberry Tart', rating: 4.5, review: 'Strawberry tart was perfect.', imageUrl: '/images/customer-20.jpg' },
  ];


const products = ['All', 'Chocolate Cake', 'Vanilla Cupcake', 'Blueberry Muffin', 'Red Velvet Cake'];
const ratings = ['All', 5, 4, 3, 2, 1];

function Reviews() {
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedProduct, setSelectedProduct] = useState('All');
  const [selectedRating, setSelectedRating] = useState('All');
  const itemsPerPage = 5;

  const handleProductChange = (event) => {
    setSelectedProduct(event.target.value);
    setCurrentPage(1); // Reset to the first page
  };

  const handleRatingChange = (event) => {
    setSelectedRating(event.target.value);
    setCurrentPage(1); // Reset to the first page
  };

  const filterReviews = (reviews) => {
    return reviews.filter(review => {
      const productMatch = selectedProduct === 'All' || review.product === selectedProduct;
      const ratingMatch = selectedRating === 'All' || review.rating >= selectedRating;
      return productMatch && ratingMatch;
    });
  };

  const filteredReviews = filterReviews(reviews);
  const totalPages = Math.ceil(filteredReviews.length / itemsPerPage);

  const handleClick = (event) => {
    setCurrentPage(Number(event.target.id));
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(i);
    }
    return pageNumbers.map(number => (
      <li
        key={number}
        id={number}
        onClick={handleClick}
        className={currentPage === number ? 'active' : ''}
      >
        {number}
      </li>
    ));
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredReviews.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="reviews-page">
      <div className="hero-section">
        <div className="hero-image">
          <img src="/images/hero-cake.jpg" alt="Reviews" />
        </div>
        <div className="hero-text">
          <h1>Reviews</h1>
        </div>
      </div>
      <div className="filters">
        <div className="filter-dropdown">
          <label htmlFor="product-filter">Filter by Product:</label>
          <select id="product-filter" value={selectedProduct} onChange={handleProductChange}>
            {products.map(product => (
              <option key={product} value={product}>{product}</option>
            ))}
          </select>
          <label htmlFor="rating-filter">Filter by Rating:</label>
          <select id="rating-filter" value={selectedRating} onChange={handleRatingChange}>
            {ratings.map(rating => (
              <option key={rating} value={rating}>{rating === 'All' ? rating : `â˜… ${rating}`}</option>
            ))}
          </select>
        </div>
        <p>Showing {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredReviews.length)} of {filteredReviews.length} results</p>
      </div>
      <div className="reviews-list">
        {currentItems.map(review => (
          <div className="review-item" key={review.id}>
            <div className="review-item-content">
              <div className="review-details">
                <p><strong>Date:</strong> {review.date}</p>
                <p><strong>Name of Customer:</strong> {review.customerName}</p>
                <p><strong>Product:</strong> {review.product}</p>
                <p><strong>Rating:</strong> {'â˜…'.repeat(review.rating)}{'â˜†'.repeat(5 - review.rating)}</p>
                <p>{review.review}</p>
              </div>
              <div className="review-image">
                <img src={review.imageUrl} alt="Customer" />
              </div>
            </div>
          </div>
        ))}
      </div>
      <ul id="page-numbers">
        {renderPageNumbers()}
        {currentPage < totalPages && <li onClick={() => setCurrentPage(currentPage + 1)}>Next</li>}
      </ul>
    </div>
  );
}

export default Reviews;
