import React, { useState } from 'react';
import './Menu.css';

const menuItems = [
  { id: 1, name: 'Chocolate Cake', price: '$15', category: 'Cakes', imageUrl: '/images/bakery-item-1.jpg' },
  { id: 2, name: 'Vanilla Cupcake', price: '$5', category: 'Cupcakes', imageUrl: '/images/bakery-item-2.jpg' },
  { id: 3, name: 'Blueberry Muffin', price: '$3', category: 'Muffins', imageUrl: '/images/bakery-item-3.jpg' },
  { id: 4, name: 'Red Velvet Cake', price: '$20', category: 'Cakes', imageUrl: '/images/bakery-item-4.jpg' },
  // Add more items as needed
];

const categories = ['All', 'Cakes', 'Cupcakes', 'Muffins'];

function Menu() {
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const itemsPerPage = 16;

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    setCurrentPage(1); // Reset to the first page
  };

  const filteredItems = selectedCategory === 'All' ? menuItems : menuItems.filter(item => item.category === selectedCategory);

  const totalPages = Math.ceil(filteredItems.length / itemsPerPage);

  const handleClick = (event) => {
    setCurrentPage(Number(event.target.id));
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(i);
    }
    return pageNumbers.map(number => (
      <li
        key={number}
        id={number}
        onClick={handleClick}
        className={currentPage === number ? 'active' : ''}
      >
        {number}
      </li>
    ));
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredItems.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="menu-page">
      <div className="hero-section">
        <div className="hero-image">
          <img src="/images/hero-cake.jpg" alt="Menu" />
        </div>
        <div className="hero-text">
          <h1>Menu</h1>
        </div>
      </div>
      <div className="filters">
        <div className="categories">
          {categories.map(category => (
            <button
              key={category}
              className={selectedCategory === category ? 'active' : ''}
              onClick={() => handleCategoryChange(category)}
            >
              {category}
            </button>
          ))}
        </div>
        <p>Showing {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredItems.length)} of {filteredItems.length} results</p>
      </div>
      <div className="menu-grid">
        {currentItems.map(item => (
          <div className="menu-item" key={item.id}>
            <div className="menu-item-image" style={{ backgroundImage: `url(${item.imageUrl})` }}></div>
            <h3>{item.name}</h3>
            <p>{item.price}</p>
            <button>Add to cart</button>
          </div>
        ))}
      </div>
      <ul id="page-numbers">
        {renderPageNumbers()}
        {currentPage < totalPages && <li onClick={() => setCurrentPage(currentPage + 1)}>Next</li>}
      </ul>
    </div>
  );
}

export default Menu;
