import React, { useState } from 'react';
import './Menu.css';

const menuItems = [
  { id: 1, name: 'Chocolate Cake', price: 15, category: 'Cakes', rating: 4.5, review: 'Delicious chocolate cake', imageUrl: '/images/bakery-item-1.jpg' },
  { id: 2, name: 'Vanilla Cupcake', price: 5, category: 'Cupcakes', rating: 4.0, review: 'Tasty vanilla cupcake', imageUrl: '/images/bakery-item-2.jpg' },
  { id: 3, name: 'Blueberry Muffin', price: 3, category: 'Muffins', rating: 4.2, review: 'Fresh blueberry muffin', imageUrl: '/images/bakery-item-3.jpg' },
  { id: 4, name: 'Red Velvet Cake', price: 20, category: 'Cakes', rating: 4.8, review: 'Delicious red velvet cake', imageUrl: '/images/bakery-item-4.jpg' },
  // Add more items as needed
];

const categories = ['All', 'Cakes', 'Cupcakes', 'Muffins'];
const priceRanges = ['All', 'Under $5', '$5-$10', 'Above $10'];
const ratings = ['All', '4 & Up', '3 & Up', '2 & Up'];

function Menu() {
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [selectedPriceRange, setSelectedPriceRange] = useState('All');
  const [selectedRating, setSelectedRating] = useState('All');
  const [sortBy, setSortBy] = useState('Relevance');
  const itemsPerPage = 16;

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
    setCurrentPage(1); // Reset to the first page
  };

  const handlePriceRangeChange = (event) => {
    setSelectedPriceRange(event.target.value);
    setCurrentPage(1); // Reset to the first page
  };

  const handleRatingChange = (event) => {
    setSelectedRating(event.target.value);
    setCurrentPage(1); // Reset to the first page
  };

  const handleSortChange = (event) => {
    setSortBy(event.target.value);
  };

  const filterItems = (items) => {
    return items.filter(item => {
      const categoryMatch = selectedCategory === 'All' || item.category === selectedCategory;
      const priceMatch = selectedPriceRange === 'All' || (selectedPriceRange === 'Under $5' && item.price < 5) || (selectedPriceRange === '$5-$10' && item.price >= 5 && item.price <= 10) || (selectedPriceRange === 'Above $10' && item.price > 10);
      const ratingMatch = selectedRating === 'All' || (selectedRating === '4 & Up' && item.rating >= 4) || (selectedRating === '3 & Up' && item.rating >= 3) || (selectedRating === '2 & Up' && item.rating >= 2);
      return categoryMatch && priceMatch && ratingMatch;
    });
  };

  const sortItems = (items) => {
    switch (sortBy) {
      case 'Price':
        return items.sort((a, b) => a.price - b.price);
      case 'Reviews':
        return items.sort((a, b) => b.rating - a.rating);
      case 'Relevance':
      default:
        return items;
    }
  };

  const filteredItems = sortItems(filterItems(menuItems));
  const totalPages = Math.ceil(filteredItems.length / itemsPerPage);

  const handleClick = (event) => {
    setCurrentPage(Number(event.target.id));
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(i);
    }
    return pageNumbers.map(number => (
      <li
        key={number}
        id={number}
        onClick={handleClick}
        className={currentPage === number ? 'active' : ''}
      >
        {number}
      </li>
    ));
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredItems.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="menu-page">
      <div className="hero-section">
        <div className="hero-image">
          <img src="/images/hero-cake.jpg" alt="Menu" />
        </div>
        <div className="hero-text">
          <h1>Menu</h1>
        </div>
      </div>
      <div className="filters">
        <div className="filter-dropdown">
          <label htmlFor="category-filter">Filter by Category:</label>
          <select id="category-filter" value={selectedCategory} onChange={handleCategoryChange}>
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
          <label htmlFor="price-filter">Filter by Price:</label>
          <select id="price-filter" value={selectedPriceRange} onChange={handlePriceRangeChange}>
            {priceRanges.map(range => (
              <option key={range} value={range}>{range}</option>
            ))}
          </select>
          <label htmlFor="rating-filter">Filter by Rating:</label>
          <select id="rating-filter" value={selectedRating} onChange={handleRatingChange}>
            {ratings.map(rating => (
              <option key={rating} value={rating}>{rating}</option>
            ))}
          </select>
        </div>
        <div className="sort-dropdown">
          <label htmlFor="sort-by">Sort by:</label>
          <select id="sort-by" value={sortBy} onChange={handleSortChange}>
            <option value="Relevance">Relevance</option>
            <option value="Price">Price</option>
            <option value="Reviews">Reviews</option>
          </select>
        </div>
        <p>Showing {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredItems.length)} of {filteredItems.length} results</p>
      </div>
      <div className="menu-grid">
        {currentItems.map(item => (
          <div className="menu-item" key={item.id}>
            <div className="menu-item-image" style={{ backgroundImage: `url(${item.imageUrl})` }}></div>
            <h3>{item.name}</h3>
            <p>{item.price}</p>
            <p>Rating: {item.rating}</p>
            <p>{item.review}</p>
            <button>Add to cart</button>
          </div>
        ))}
      </div>
      <ul id="page-numbers">
        {renderPageNumbers()}
        {currentPage < totalPages && <li onClick={() => setCurrentPage(currentPage + 1)}>Next</li>}
      </ul>
    </div>
  );
}

export default Menu;
