import React, { useState } from 'react';
import './Menu.css';

const menuItems = [
  { id: 1, name: 'Type of bakery item', price: 'Price' },
  { id: 2, name: 'Type of bakery item', price: 'Price' },
  { id: 3, name: 'Type of bakery item', price: 'Price' },
  { id: 4, name: 'Type of bakery item', price: 'Price' },
  // Add more items as needed
];

function Menu() {
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 16;
  const totalPages = Math.ceil(menuItems.length / itemsPerPage);

  const handleClick = (event) => {
    setCurrentPage(Number(event.target.id));
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(i);
    }
    return pageNumbers.map(number => (
      <li
        key={number}
        id={number}
        onClick={handleClick}
        className={currentPage === number ? 'active' : ''}
      >
        {number}
      </li>
    ));
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = menuItems.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="menu-page">
      <div className="hero-section">
        <img src="/images/hero-cake.jpg" alt="Menu" />
        <div className="hero-text">
          <h1>Menu</h1>
        </div>
      </div>
      <div className="filters">
        <button>Filter</button>
        <button>Sort by Relevance ^</button>
        <p>Showing {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, menuItems.length)} of {menuItems.length} results</p>
      </div>
      <div className="menu-grid">
        {currentItems.map(item => (
          <div className="menu-item" key={item.id}>
            <div className="menu-item-image"></div>
            <h3>{item.name}</h3>
            <p>{item.price}</p>
            <button>Add to cart</button>
          </div>
        ))}
      </div>
      <ul id="page-numbers">
        {renderPageNumbers()}
        {currentPage < totalPages && <li onClick={() => setCurrentPage(currentPage + 1)}>Next</li>}
      </ul>
    </div>
  );
}

export default Menu;
