import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import './Menu.css';

const menuItems = [
  { id: 1, name: 'Chocolate Cake', price: 15, category: 'Cakes', rating: 4.5, review: 'Amazing chocolate cake!', imageUrl: '/images/bakery-item-1.jpg' },
  // ... other items
];

function Menu() {
  const [currentPage, setCurrentPage] = useState(1);
  const { dispatch } = useCart();
  const itemsPerPage = 16;

  const addToCart = (item) => {
    dispatch({ type: 'ADD_TO_CART', item });
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = menuItems.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="menu-page">
      <div className="hero-section">
        <div className="hero-image">
          <img src="/images/hero-cake.jpg" alt="Menu" />
        </div>
        <div className="hero-text">
          <h1>Menu</h1>
        </div>
      </div>
      <div className="filters">
        <p>Showing {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, menuItems.length)} of {menuItems.length} results</p>
      </div>
      <div className="menu-grid">
        {currentItems.map(item => (
          <div className="menu-item" key={item.id}>
            <div className="menu-item-image" style={{ backgroundImage: `url(${item.imageUrl})` }}></div>
            <h3>{item.name}</h3>
            <p>{item.price}</p>
            <button onClick={() => addToCart(item)}>Add to cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Menu;
