import React, { useState } from 'react';
import './Reviews.css';

const reviews = [
  { id: 1, date: '2024-06-01', customerName: 'Jane Doe', product: 'Chocolate Cake', rating: 5, review: 'Amazing chocolate cake!', imageUrl: '/images/customer-1.jpg' },
  { id: 2, date: '2024-06-02', customerName: 'John Smith', product: 'Vanilla Cupcake', rating: 4, review: 'Loved the vanilla cupcake!', imageUrl: '/images/customer-2.jpg' },
  { id: 3, date: '2024-06-03', customerName: 'Alice Johnson', product: 'Blueberry Muffin', rating: 4.2, review: 'The blueberry muffin was fresh and delicious.', imageUrl: '/images/customer-3.jpg' },
  { id: 4, date: '2024-06-04', customerName: 'Michael Brown', product: 'Red Velvet Cake', rating: 4.8, review: 'The red velvet cake was divine!', imageUrl: '/images/customer-4.jpg' },
  // Add more reviews as needed
];

const products = ['All', 'Chocolate Cake', 'Vanilla Cupcake', 'Blueberry Muffin', 'Red Velvet Cake'];
const ratings = ['All', 5, 4, 3, 2, 1];

function Reviews() {
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedProduct, setSelectedProduct] = useState('All');
  const [selectedRating, setSelectedRating] = useState('All');
  const itemsPerPage = 5;

  const handleProductChange = (event) => {
    setSelectedProduct(event.target.value);
    setCurrentPage(1); // Reset to the first page
  };

  const handleRatingChange = (event) => {
    setSelectedRating(event.target.value);
    setCurrentPage(1); // Reset to the first page
  };

  const filterReviews = (reviews) => {
    return reviews.filter(review => {
      const productMatch = selectedProduct === 'All' || review.product === selectedProduct;
      const ratingMatch = selectedRating === 'All' || review.rating >= selectedRating;
      return productMatch && ratingMatch;
    });
  };

  const filteredReviews = filterReviews(reviews);
  const totalPages = Math.ceil(filteredReviews.length / itemsPerPage);

  const handleClick = (event) => {
    setCurrentPage(Number(event.target.id));
  };

  const renderPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(i);
    }
    return pageNumbers.map(number => (
      <li
        key={number}
        id={number}
        onClick={handleClick}
        className={currentPage === number ? 'active' : ''}
      >
        {number}
      </li>
    ));
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredReviews.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="reviews-page">
      <div className="hero-section">
        <div className="hero-image">
          <img src="/images/hero-cake.jpg" alt="Reviews" />
        </div>
        <div className="hero-text">
          <h1>Reviews</h1>
        </div>
      </div>
      <div className="filters">
        <div className="filter-dropdown">
          <label htmlFor="product-filter">Filter by Product:</label>
          <select id="product-filter" value={selectedProduct} onChange={handleProductChange}>
            {products.map(product => (
              <option key={product} value={product}>{product}</option>
            ))}
          </select>
          <label htmlFor="rating-filter">Filter by Rating:</label>
          <select id="rating-filter" value={selectedRating} onChange={handleRatingChange}>
            {ratings.map(rating => (
              <option key={rating} value={rating}>{rating === 'All' ? rating : `★ ${rating}`}</option>
            ))}
          </select>
        </div>
        <p>Showing {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredReviews.length)} of {filteredReviews.length} results</p>
      </div>
      <div className="reviews-list">
        {currentItems.map(review => (
          <div className="review-item" key={review.id}>
            <div className="review-item-content">
              <div className="review-details">
                <p><strong>Date:</strong> {review.date}</p>
                <p><strong>Name of Customer:</strong> {review.customerName}</p>
                <p><strong>Product:</strong> {review.product}</p>
                <p><strong>Rating:</strong> {'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}</p>
                <p>{review.review}</p>
              </div>
              <div className="review-image">
                <img src={review.imageUrl} alt="Customer" />
              </div>
            </div>
          </div>
        ))}
      </div>
      <ul id="page-numbers">
        {renderPageNumbers()}
        {currentPage < totalPages && <li onClick={() => setCurrentPage(currentPage + 1)}>Next</li>}
      </ul>
    </div>
  );
}

export default Reviews;
